function back( $pos ){
	global $P , $days , $esc , $S , $T , $courseName , $rand, $colorCurso, $contador , $mapColor , $mapCourseSections,$indica;
	if( $pos == count( $P ) )
	{
		$str = "";
		$str .= "<div class='row-fluid'><div class='box span11'>";
		$str .= "<div class='box-content'>";
		$str .= "<table class='table table-striped table-bordered bootstrap-datatable '>
			<thead>";
		$str .= "<tr>";
		$str .= "<th class='cabecera'>HORARIO</th>";
		// $str = "";
		$gg = 0;
		for( $i = 0 ; $i < 6 ; $i++ )
			switch ($days[ $i ]) {
				case 'LU':
					$str .= "<th class='cabecera'>LUNES</th>";
					break;
				case 'MA':
					$str .= "<th class='cabecera'>MARTES</th>";
					break;
				case 'MI':
					$str .= "<th class='cabecera'>MIERCOLES</th>";
					break;
				case 'JU':
					$str .= "<th class='cabecera'>JUEVES</th>";
					break;
				case 'VI':
					$str .= "<th class='cabecera'>VIERNES</th>";
					break;
				case 'SA':
					$str .= "<th class='cabecera'>SABADO</th>";
					break;
			}
		$str .= "</tr>";
		$str .= "</thead>";
		$indica = $indica + 1;
		$str .= "<tbody class='bCurso".$indica."'>";
		for( $i = 8 ; $i <= 22 ; $i++ ){
			$str .= "<tr>";
			$str .= "<td class='hora'>".$i.":00</td>";
			for( $j = 0 ; $j < 6 ; $j++ ){
				$len = count( $S[ $j ][ $i ] );
				$cad = "";
				if( $len == 0 ){
					$cad = "<td class='cuerpo'></td>";
				}else if( $len == 1 ){
					$valor = $S[$j][$i][0];
					$valor = substr($valor,0,6);
					$color = $mapColor[$valor];
					$cad = "<td class='cuerpo cuerpoDia' style='background-color:".$color."'>".$S[ $j ][ $i ][ 0 ]."</td>";
				}else{
					$gg = 1;
					$str .= "<td class='cuerpo'></td>";
				}
				$str .= $cad;
			}
			$str .= "</tr>";
		}
		$str .= "</tbody></table>";
		$str .= "</div>";
		$str .= "</div>";
		$str .= "</div>";
		$str .= "<button class='btn btn-primary matricular' value='".$indica."'>MATRICULARME</button>";
		//$str .= "<div class='container' id='MostrarMatricula".$indica."'></div>";
		if( !$gg ) echo $str;
		return;
	}
	$it1 = $P[ $pos ];
	foreach( $T[ $it1 ] as $sect => $V ){
		if( $sect == null ) continue;
		if( $V== null ) continue;
		$doit = 0;
		if( !array_key_exists( $sect , $mapCourseSections[ $courseName[ $P[ $pos ] ] ] ) ) continue;
		if( !$mapCourseSections[ $courseName[ $P[ $pos ] ] ][ $sect ] ) continue;
		foreach( $V as $vec ){
			if( $vec == null ) continue;
			$a = $vec[ 0 ];
			$b = $vec[ 1 ];
			$S[ $a ][ $b ][] = (($courseName[ $P[ $pos ] ]).$sect);
			$doit = 1;
		}
		back( $pos + 1 );
		if( $doit == 0 ) continue;
		foreach( $V as $vec ){
			if( $vec == null ) continue;
			$a = $vec[ 0 ];
			$b = $vec[ 1 ];
			array_pop( $S[ $a ][ $b ] );
		}
	}
}
for( $j = 0 ; $j < 6 ; $j++ ) $S[ $j ] = array(array());
