foreach( $temp as $str )
{
	$sections = substr( $str , 6 );
	$str = substr( $str , 0 , 6 );
	//var_dump( $str );
	//var_dump( $sections );
	if( !array_key_exists( $str , $mapCourseSections ) ) $mapCourseSections[ $str ] = array();
	for( $i = 0 ; $i < strlen( $sections ) ; $i++ ){
		$cad = substr( $sections , $i , 1 );
		//var_dump( $cad );
		if( !array_key_exists( $cad , $mapCourseSections[ $str ] ) ) $mapCourseSections[ $str ][ $cad ] = 1;
	}
	$P[] = $mapaCourse[ $str ];
	$mapColor[ $str ] =  '#'.$rand[rand(0,15)].$rand[rand(0,15)].$rand[rand(0,15)].$rand[rand(0,15)].$rand[rand(0,15)].$rand[rand(0,15)];
}
